discussion.pre:
#include <iostream> 
#include <fstream>
#include <streambuf> 

typedef char C;
int main ()
{
  std::ifstream s("filename", std::ios::in);
  std::istreambuf_iterator<char> i(s);

  (*i).~C();  // This is well-formed...
  i->~C();  // ... so this should be supported!
}

